def big_sum(p1, p2):
	
	la = len(p1)
	lb = len(p2)
	
	if la > lb:
		a = [i for i in p1]
		b = [i for i in p2]
	else:
		b = [i for i in p1]
		a = [i for i in p2]
	
	temp = 0
	idx = 0
	
	
	for j in range(len(b)):
		temp = a[idx] + b[idx] + temp
		a[idx] = temp % 10
		temp = temp // 10
		idx += 1	
	while temp > 0:
		if  idx >= len(a):
			a.append(temp % 10)
			temp = temp // 10
		else:
			a[idx] += temp % 10
			temp = temp // 10
		
		idx += 1		

	return a
		
def big_product(p1, p2):
	
	a = [(i) for i in p1]
	b = [(i) for i in p2]
	
	count_zero = 0
	while a[0] == 0:
		count_zero += 1
		a.pop(0)
	
	while b[0] == 0:
		count_zero += 1
		b.pop(0) 
		
	la = len(a)
	lb = len(b)
	
	product = [(0) for i in range(len(a+b)-1)]
	
	idx = 0
	for i in range(lb):
		idx = i
		temp = 0
		for j in range(la):
			temp = a[j]*b[i] + product[idx] + temp
			
			product[idx] = temp%10
			temp = temp // 10
			
			idx += 1
			
		while temp > 0:
			product.append(temp%10)
			temp = temp // 10
			
		
			
	product = [(0) for i in range(count_zero)] + product
	return product
	
# all operations in reverse order

total = [0]
for i in range(1000):	
	print(i+1)
	num = list(map(int, list(str(i+1))))
	num.reverse()
	product = [(i) for i in num]
	
	for j in range(i):
		product = big_product(product, num)
	total = big_sum(product, total)
total.reverse()
print(''.join(list(map(str, total))))

